import createMDX from '@next/mdx';
import rehypePrettyCode from 'rehype-pretty-code';

/** @type {import('rehype-pretty-code').Options} */
const rehypePrettyCodeOptions = {
  theme: 'github-dark',
  keepBackground: true,
  onVisitLine(node) {
    // Prevent lines from collapsing in `display: grid` mode, and
    // allow empty lines to be copy/pasted
    if (node.children.length === 0) {
      node.children = [{ type: 'text', value: ' ' }];
    }
  },
  onVisitHighlightedLine(node) {
    node.properties.className = node.properties.className || [];
    node.properties.className.push('highlighted');
  },
  onVisitHighlightedChars(node) {
    node.properties.className = ['word-highlighted'];
  },
};

const withMDX = createMDX({
  extension: /\.mdx?$/,
  options: {
    remarkPlugins: [],
    rehypePlugins: [[rehypePrettyCode, rehypePrettyCodeOptions]],
  },
});

/** @type {import('next').NextConfig} */
const nextConfig = {
  pageExtensions: ['js', 'jsx', 'md', 'mdx', 'ts', 'tsx'],
  reactStrictMode: true,
};

export default withMDX(nextConfig);

import type { MDXComponents } from 'mdx/types';
import { CodeBlock } from '@/components/mdx/CodeBlock';
import { Callout } from '@/components/mdx/Callout';
import { ApiTable } from '@/components/mdx/ApiTable';

export function useMDXComponents(components: MDXComponents): MDXComponents {
  return {
    // Default components with styling
    h1: ({ children }) => (
      <h1 className="text-4xl font-bold text-white mb-6 mt-8 first:mt-0">{children}</h1>
    ),
    h2: ({ children }) => (
      <h2 className="text-2xl font-semibold text-white mb-4 mt-10 pb-2 border-b border-neutral-800">{children}</h2>
    ),
    h3: ({ children }) => (
      <h3 className="text-xl font-semibold text-white mb-3 mt-8">{children}</h3>
    ),
    h4: ({ children }) => (
      <h4 className="text-lg font-semibold text-white mb-2 mt-6">{children}</h4>
    ),
    p: ({ children }) => (
      <p className="text-neutral-300 leading-7 mb-4">{children}</p>
    ),
    a: ({ href, children }) => (
      <a href={href} className="text-blue-400 hover:text-blue-300 underline underline-offset-2">
        {children}
      </a>
    ),
    ul: ({ children }) => (
      <ul className="list-disc list-inside text-neutral-300 mb-4 space-y-1 ml-2">{children}</ul>
    ),
    ol: ({ children }) => (
      <ol className="list-decimal list-inside text-neutral-300 mb-4 space-y-1 ml-2">{children}</ol>
    ),
    li: ({ children }) => (
      <li className="leading-7">{children}</li>
    ),
    blockquote: ({ children }) => (
      <blockquote className="border-l-4 border-neutral-700 pl-4 italic text-neutral-400 my-4">
        {children}
      </blockquote>
    ),
    code: ({ children, className }) => {
      // Check if it's an inline code or code block
      const isCodeBlock = className?.includes('language-');

      if (isCodeBlock) {
        // This is handled by rehype-pretty-code
        return <code className={className}>{children}</code>;
      }

      // Inline code styling
      return (
        <code className="bg-neutral-800 text-neutral-200 px-1.5 py-0.5 rounded text-sm font-mono">
          {children}
        </code>
      );
    },
    pre: ({ children, ...props }) => {
      return (
        <pre {...props} className="relative group">
          {children}
        </pre>
      );
    },
    table: ({ children }) => (
      <div className="overflow-x-auto my-6">
        <table className="w-full border-collapse text-sm">{children}</table>
      </div>
    ),
    th: ({ children }) => (
      <th className="border border-neutral-800 bg-neutral-900 px-4 py-2 text-left text-neutral-200 font-semibold">
        {children}
      </th>
    ),
    td: ({ children }) => (
      <td className="border border-neutral-800 px-4 py-2 text-neutral-300">{children}</td>
    ),
    hr: () => <hr className="border-neutral-800 my-8" />,
    // Custom components
    Callout,
    CodeBlock,
    ApiTable,
    ...components,
  };
}

'use client';

import { useState, useRef, useEffect } from 'react';

interface CodeBlockProps {
  children: React.ReactNode;
  language?: string;
  title?: string;
  showLineNumbers?: boolean;
}

export function CodeBlock({ 
  children, 
  language = 'typescript', 
  title, 
  showLineNumbers = false 
}: CodeBlockProps) {
  const [copied, setCopied] = useState(false);
  const preRef = useRef<HTMLPreElement>(null);

  const copyToClipboard = async () => {
    if (preRef.current) {
      const code = preRef.current.textContent || '';
      await navigator.clipboard.writeText(code);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  return (
    <div className="relative group my-4 rounded-lg overflow-hidden bg-[#0a0a0a] border border-neutral-800">
      {title && (
        <div className="flex items-center justify-between px-4 py-2.5 bg-[#111] border-b border-neutral-800">
          <span className="text-sm text-neutral-400 font-mono">{title}</span>
          <span className="text-xs text-neutral-500 uppercase tracking-wider">{language}</span>
        </div>
      )}
      <div className="relative">
        <button
          onClick={copyToClipboard}
          className="absolute top-3 right-3 z-10 p-2 rounded-md bg-neutral-800/80 text-neutral-400 hover:text-neutral-100 hover:bg-neutral-700 opacity-0 group-hover:opacity-100 transition-all duration-200"
          aria-label="Copy code"
        >
          {copied ? (
            <svg className="w-4 h-4 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
            </svg>
          ) : (
            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
            </svg>
          )}
        </button>
        <pre 
          ref={preRef}
          className={`p-4 overflow-x-auto text-sm leading-relaxed ${showLineNumbers ? 'line-numbers' : ''} ${!title ? 'rounded-lg' : ''}`}
          data-language={language}
        >
          {typeof children === 'string' ? (
            <code className={`language-${language}`}>{children}</code>
          ) : (
            children
          )}
        </pre>
      </div>
    </div>
  );
}

// Wrapper for pre tags that come from MDX/rehype-pretty-code
