  const [copied, setCopied] = useState(false);
  const preRef = useRef<HTMLPreElement>(null);

  const copyToClipboard = async () => {
    if (preRef.current) {
      const code = preRef.current.textContent || '';
      await navigator.clipboard.writeText(code);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  return (
    <div className="relative group my-4">
      <button
        onClick={copyToClipboard}
        className="absolute top-3 right-3 z-10 p-2 rounded-md bg-neutral-800/80 text-neutral-400 hover:text-neutral-100 hover:bg-neutral-700 opacity-0 group-hover:opacity-100 transition-all duration-200"
        aria-label="Copy code"
      >
        {copied ? (
          <svg className="w-4 h-4 text-green-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
          </svg>
        ) : (
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
          </svg>
        )}
      </button>
      <pre 
        ref={preRef}
        {...props}
        className="rounded-lg border border-neutral-800 bg-[#0a0a0a] p-4 overflow-x-auto text-sm leading-relaxed"
      >
        {children}
      </pre>
    </div>
  );
}

import { ReactNode } from 'react';

type CalloutType = 'info' | 'warning' | 'tip' | 'error';

interface CalloutProps {
  type?: CalloutType;
  title?: string;
  children: ReactNode;
}

const styles: Record<CalloutType, { bg: string; border: string; icon: string; iconColor: string }> = {
  info: {
    bg: 'bg-blue-500/5',
    border: 'border-blue-500/20',
    iconColor: 'text-blue-400',
    icon: 'M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z',
  },
  warning: {
    bg: 'bg-yellow-500/5',
    border: 'border-yellow-500/20',
    iconColor: 'text-yellow-400',
    icon: 'M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z',
  },
  tip: {
    bg: 'bg-green-500/5',
    border: 'border-green-500/20',
    iconColor: 'text-green-400',
    icon: 'M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z',
  },
  error: {
    bg: 'bg-red-500/5',
    border: 'border-red-500/20',
    iconColor: 'text-red-400',
    icon: 'M10 14l2-2m0 0l2-2m-2 2l-2-2m2 2l2 2m7-2a9 9 0 11-18 0 9 9 0 0118 0z',
  },
};

const defaultTitles: Record<CalloutType, string> = {
  info: 'Note',
  warning: 'Warning',
  tip: 'Tip',
  error: 'Error',
};

export function Callout({ type = 'info', title, children }: CalloutProps) {
  const style = styles[type];
  const displayTitle = title || defaultTitles[type];

  return (
    <div className={`my-6 rounded-lg border ${style.bg} ${style.border} p-4`}>
      <div className="flex items-start gap-3">
        <svg 
          className={`w-5 h-5 mt-0.5 flex-shrink-0 ${style.iconColor}`} 
          fill="none" 
          stroke="currentColor" 
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={style.icon} />
        </svg>
        <div className="flex-1 min-w-0">
          <p className={`font-semibold text-sm mb-1 ${style.iconColor}`}>
            {displayTitle}
          </p>
          <div className="text-neutral-300 text-sm leading-relaxed [&>p]:m-0 [&>p:last-child]:mb-0">
            {children}
          </div>
        </div>
      </div>
    </div>
  );
}

import { ReactNode } from 'react';

interface ApiParameter {
  name: string;
  type: string;
  description: string;
  required?: boolean;
  default?: string;
}

interface ApiTableProps {
  title?: string;
  description?: string;
  parameters?: ApiParameter[];
  returns?: {
    type: string;
    description: string;
  };
  children?: ReactNode;
}

export function ApiTable({ title, description, parameters, returns, children }: ApiTableProps) {
  return (
    <div className="my-8 rounded-lg border border-neutral-800 overflow-hidden">
      {title && (
        <div className="px-4 py-3 bg-[#111] border-b border-neutral-800">
          <code className="text-base font-semibold text-white">{title}</code>
          {description && (
            <p className="text-sm text-neutral-400 mt-1">{description}</p>
          )}
        </div>
      )}

      {parameters && parameters.length > 0 && (
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="bg-neutral-900/50">
                <th className="px-4 py-2.5 text-left text-xs font-semibold text-neutral-400 uppercase tracking-wider">Parameter</th>
                <th className="px-4 py-2.5 text-left text-xs font-semibold text-neutral-400 uppercase tracking-wider">Type</th>
                <th className="px-4 py-2.5 text-left text-xs font-semibold text-neutral-400 uppercase tracking-wider">Description</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-neutral-800">
              {parameters.map((param, index) => (
                <tr key={index} className="hover:bg-neutral-900/30 transition-colors">
                  <td className="px-4 py-3">
                    <code className="text-sm text-blue-400">{param.name}</code>
                    {param.required !== false && (
                      <span className="ml-1 text-red-400 text-xs">*</span>
                    )}
                  </td>
                  <td className="px-4 py-3">
                    <code className="text-sm text-purple-400">{param.type}</code>
                    {param.default && (
                      <span className="text-xs text-neutral-500 ml-2">= {param.default}</span>
                    )}
                  </td>
                  <td className="px-4 py-3 text-sm text-neutral-300">{param.description}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {returns && (
        <div className="px-4 py-3 bg-neutral-900/30 border-t border-neutral-800">
          <span className="text-xs font-semibold text-neutral-400 uppercase tracking-wider">Returns</span>
          <div className="mt-1.5 flex items-start gap-2">
            <code className="text-sm text-green-400">{returns.type}</code>
            <span className="text-sm text-neutral-400">— {returns.description}</span>
          </div>
        </div>
      )}

      {children && (
        <div className="px-4 py-3 border-t border-neutral-800 text-sm text-neutral-300">
          {children}
        </div>
      )}
    </div>
  );
}

// Simpler API signature component for inline use
interface ApiSignatureProps {
  name: string;
  signature: string;
  description?: string;
}

export function ApiSignature({ name, signature, description }: ApiSignatureProps) {
  return (
    <div className="my-4 p-4 rounded-lg bg-[#0a0a0a] border border-neutral-800">
      <code className="text-blue-400 font-semibold">{name}</code>
      <code className="text-neutral-400 ml-1">{signature}</code>
      {description && (
        <p className="text-sm text-neutral-400 mt-2">{description}</p>
      )}
    </div>
  );
}

export { CodeBlock, Pre } from './CodeBlock';
export { Callout } from './Callout';
export { ApiTable, ApiSignature } from './ApiTable';

import type { MDXComponents } from 'mdx/types';
import { CodeBlock, Pre } from '@/components/mdx/CodeBlock';
import { Callout } from '@/components/mdx/Callout';
import { ApiTable, ApiSignature } from '@/components/mdx/ApiTable';

export function useMDXComponents(components: MDXComponents): MDXComponents {
  return {
    // Default components with Vercel-inspired styling
    h1: ({ children }) => (
      <h1 className="text-4xl font-bold text-white mb-6 mt-8 first:mt-0 tracking-tight">{children}</h1>
    ),
    h2: ({ children }) => (
      <h2 className="text-2xl font-semibold text-white mb-4 mt-12 pb-2 border-b border-neutral-800 tracking-tight">{children}</h2>
    ),
    h3: ({ children }) => (
      <h3 className="text-xl font-semibold text-white mb-3 mt-8 tracking-tight">{children}</h3>
    ),
    h4: ({ children }) => (
      <h4 className="text-lg font-semibold text-white mb-2 mt-6 tracking-tight">{children}</h4>
    ),
    p: ({ children }) => (
      <p className="text-neutral-300 leading-7 mb-4">{children}</p>
    ),
    a: ({ href, children }) => (
      <a 
        href={href} 
        className="text-blue-400 hover:text-blue-300 underline underline-offset-4 decoration-blue-400/30 hover:decoration-blue-300/50 transition-colors"
      >
        {children}
      </a>
    ),
    ul: ({ children }) => (
      <ul className="list-disc text-neutral-300 mb-4 space-y-2 pl-6">{children}</ul>
    ),
    ol: ({ children }) => (
      <ol className="list-decimal text-neutral-300 mb-4 space-y-2 pl-6">{children}</ol>
    ),
    li: ({ children }) => (
      <li className="leading-7 pl-1">{children}</li>
    ),
    blockquote: ({ children }) => (
      <blockquote className="border-l-2 border-neutral-700 pl-4 italic text-neutral-400 my-6">
        {children}
      </blockquote>
    ),
    code: ({ children, className }) => {
      // Check if it's a code block (handled by pre wrapper via rehype-pretty-code)
      const isCodeBlock = className?.includes('language-');

      if (isCodeBlock) {
        return <code className={className}>{children}</code>;
      }

      // Inline code styling
      return (
        <code className="bg-neutral-800/60 text-neutral-200 px-1.5 py-0.5 rounded text-[0.875em] font-mono border border-neutral-700/50">
          {children}
        </code>
      );
    },
    pre: Pre,
    table: ({ children }) => (
      <div className="overflow-x-auto my-6 rounded-lg border border-neutral-800">
        <table className="w-full border-collapse text-sm">{children}</table>
      </div>
    ),
    thead: ({ children }) => (
      <thead className="bg-neutral-900/50">{children}</thead>
    ),
    th: ({ children }) => (
      <th className="px-4 py-3 text-left text-xs font-semibold text-neutral-400 uppercase tracking-wider border-b border-neutral-800">
        {children}
      </th>
    ),
    td: ({ children }) => (
      <td className="px-4 py-3 text-neutral-300 border-b border-neutral-800">{children}</td>
    ),
    hr: () => <hr className="border-neutral-800 my-10" />,
    strong: ({ children }) => (
      <strong className="font-semibold text-white">{children}</strong>
    ),
    // Custom components
    Callout,
    CodeBlock,
    ApiTable,
    ApiSignature,
    ...components,
  };
}

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Vercel-inspired colors */
    --background: #000000;
    --background-secondary: #0a0a0a;
    --background-tertiary: #111111;

    --foreground: #fafafa;
    --foreground-secondary: #a1a1a1;
    --foreground-muted: #666666;

    --border: #333333;
    --border-hover: #444444;

    --accent-blue: #0070f3;
    --accent-blue-hover: #0366d6;
    --accent-green: #50e3c2;
    --accent-red: #ee0000;
    --accent-yellow: #f5a623;
  }
}

/* Custom scrollbar for dark theme */
::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

::-webkit-scrollbar-track {
  background: transparent;
}

::-webkit-scrollbar-thumb {
  background: #333;
  border-radius: 3px;
}

::-webkit-scrollbar-thumb:hover {
  background: #444;
}

/* Smooth scrolling */
html {
  scroll-behavior: smooth;
}

/* Selection color */
::selection {
  background: rgba(0, 112, 243, 0.3);
}

/* Code block styling from rehype-pretty-code */
pre {
  @apply text-[13px] leading-[1.7];
}

pre > code {
  display: grid;
  counter-reset: line;
}

pre > code > [data-line] {
  @apply px-4;
}

pre [data-line-numbers] > [data-line]::before {
  counter-increment: line;
  content: counter(line);
  @apply inline-block w-6 mr-4 text-right text-neutral-600;
}

/* Line highlighting */
pre [data-line].highlighted {
  @apply bg-blue-500/10 border-l-2 border-blue-500;
}

/* Word highlighting */
pre [data-highlighted-chars] {
  @apply bg-blue-500/20 rounded px-1 py-0.5;
}

/* Code block themes - integrate with rehype-pretty-code */
[data-rehype-pretty-code-figure] {
  @apply my-4;
}

[data-rehype-pretty-code-figure] pre {
  @apply rounded-lg border border-neutral-800 bg-[#0a0a0a] p-0 overflow-x-auto;
}

[data-rehype-pretty-code-figure] pre > code {
  @apply py-4;
}

[data-rehype-pretty-code-figure] [data-line] {
  @apply px-4;
}

/* Title for code blocks */
[data-rehype-pretty-code-title] {
  @apply text-sm text-neutral-400 font-mono px-4 py-2.5 bg-[#111] border border-neutral-800 border-b-0 rounded-t-lg;
}

[data-rehype-pretty-code-title] + pre {
  @apply rounded-t-none;
}

/* Caption below code blocks */
[data-rehype-pretty-code-caption] {
  @apply text-xs text-neutral-500 mt-2;
}

/* Focus styles */
:focus-visible {
  @apply outline-none ring-2 ring-blue-500 ring-offset-2 ring-offset-black;
}

/* Transitions */
* {
  @apply transition-colors duration-150;
}

/* Content spacing */
.prose-content > * + * {
  @apply mt-4;
}

.prose-content > h2 + * {
  @apply mt-4;
}

.prose-content > h3 + * {
  @apply mt-3;
}

/* Card hover effects */
.card-hover {
  @apply transition-all duration-200;
}

.card-hover:hover {
  @apply border-neutral-700 bg-neutral-900/50;
}

/* Link animations */
.link-animated {
  @apply relative;
}

.link-animated::after {
  content: '';
  @apply absolute left-0 bottom-0 w-0 h-px bg-current transition-all duration-200;
}

.link-animated:hover::after {
  @apply w-full;
}

