'use client';

import { useState, useCallback } from 'react';

interface CodeBlockProps {
  children: string;
  language?: string;
  filename?: string;
  showLineNumbers?: boolean;
  highlightLines?: number[];
}

const languageLabels: Record<string, string> = {
  typescript: 'TypeScript',
  ts: 'TypeScript',
  javascript: 'JavaScript',
  js: 'JavaScript',
  tsx: 'TSX',
  jsx: 'JSX',
  wgsl: 'WGSL',
  bash: 'Terminal',
  shell: 'Terminal',
  sh: 'Terminal',
  json: 'JSON',
  css: 'CSS',
  html: 'HTML',
};

export function CodeBlock({
  children,
  language = 'text',
  filename,
  showLineNumbers = false,
  highlightLines = [],
}: CodeBlockProps) {
  const [copied, setCopied] = useState(false);

  const handleCopy = useCallback(async () => {
    await navigator.clipboard.writeText(children.trim());
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  }, [children]);

  const lines = children.trim().split('\n');
  const label = filename || languageLabels[language] || language.toUpperCase();

  return (
    <div className="code-block group relative my-6 overflow-hidden rounded-lg border border-[#333] bg-[#0a0a0a]">
      {/* Header */}
      <div className="flex items-center justify-between border-b border-[#333] px-4 py-2">
        <span className="text-xs font-medium text-[#666]">{label}</span>
        <button
          onClick={handleCopy}
          className="flex items-center gap-1.5 rounded px-2 py-1 text-xs text-[#666] transition-colors hover:bg-[#1a1a1a] hover:text-[#888]"
          aria-label="Copy code"
        >
          {copied ? (
            <>
              <CheckIcon className="h-3.5 w-3.5" />
              <span>Copied!</span>
            </>
          ) : (
            <>
              <CopyIcon className="h-3.5 w-3.5" />
              <span>Copy</span>
            </>
          )}
        </button>
      </div>

      {/* Code content */}
      <div className="overflow-x-auto">
        <pre className="p-4 text-sm leading-relaxed">
          <code className={`language-${language}`}>
            {lines.map((line, index) => {
              const lineNumber = index + 1;
              const isHighlighted = highlightLines.includes(lineNumber);

              return (
                <div
                  key={index}
                  className={`${isHighlighted ? 'bg-blue-500/10 -mx-4 px-4' : ''} ${
                    showLineNumbers ? 'flex' : ''
                  }`}
                >
                  {showLineNumbers && (
                    <span className="mr-4 inline-block w-8 select-none text-right text-[#444]">
                      {lineNumber}
                    </span>
                  )}
                  <span className="flex-1">
                    <HighlightedLine line={line} language={language} />
                  </span>
                </div>
              );
            })}
          </code>
        </pre>
      </div>
    </div>
  );
}

// Simple syntax highlighting by parsing tokens
function HighlightedLine({ line, language }: { line: string; language: string }) {
  // Apply simple token-based highlighting
  const tokens = tokenize(line, language);

  return (
    <>
      {tokens.map((token, i) => (
        <span key={i} className={token.className}>
          {token.text}
        </span>
      ))}
      {'\n'}
    </>
  );
}

interface Token {
  text: string;
  className: string;
}

function tokenize(line: string, language: string): Token[] {
  const tokens: Token[] = [];

  // Keywords for different languages
  const tsKeywords = [
    'const', 'let', 'var', 'function', 'async', 'await', 'return', 'import', 'export',
    'from', 'if', 'else', 'for', 'while', 'class', 'interface', 'type', 'extends',
    'implements', 'new', 'this', 'true', 'false', 'null', 'undefined', 'try', 'catch',
    'throw', 'finally', 'typeof', 'instanceof', 'default', 'break', 'continue', 'switch',
    'case', 'do', 'in', 'of', 'void', 'delete', 'static', 'readonly', 'private', 'public',
    'protected', 'abstract', 'enum', 'namespace', 'module', 'declare', 'as', 'is'
  ];

  const wgslKeywords = [
    'fn', 'var', 'let', 'const', 'struct', 'if', 'else', 'for', 'while', 'loop',
    'return', 'break', 'continue', 'discard', 'switch', 'case', 'default', 'true', 'false',
